namespace yii\easyii\widgets;


use yii\base\Widget;

class MultilingualForm extends Widget
{
    public $model;
    public $form;

    /**
     * @var array
     */
    public $fields;

    /**
     * @var array
     */
    public $slug;

    public function init()
    {
        parent::init(); // TODO: Change the autogenerated stub
    }

    public function run()
    {
        return $this->render('multilingual-form', [
            'model' => $this->model,
            'form' => $this->form,
            'fields' => $this->fields,
            'slug' => $this->slug,
        ]);
    }
}